<template>
	<view>
		<!-- 地址 -->
		<view class="address-section" @click="addAddress()">
			<view class="order-content">
				<text class="yticon icon-shouhuodizhi"></text>
				<view class="cen" v-if="addressData.location">
					<view class="top">
						<text class="name">{{addressData.name}}</text>
						<text class="mobile">{{addressData.cellphone}}</text>
					</view>
					<text class="address">{{addressData.location}}
					<block v-if="addressData.address">({{addressData.address}})</block>
					{{addressData.house}}</text>
				</view>
				<view class="cen" v-else>
					<text class="text-left padding-left">选择地址</text>
				</view>
				<text class="yticon icon-you"></text>
			</view>
			<image class="a-bg" src="data:image/png;base64,"></image>
		</view>

		<view class="goods-section">
			<!-- 商品列表 -->
			<view class="g-item padding-top-sm" v-for="(item,index) in goodList" :key="index">
				<image :src="item.img | smallImage" lazy-load></image>
				<view class="right">
					<text class="title clamp">{{item.name}}</text>
					<text class="spec">{{item.specification}}</text>
					<view class="price-box">
						<text class="price">￥{{item.price}}</text>
						<text class="number">x {{item.number}}</text>
					</view>
				</view>
			</view>
		</view>
		<!-- 金额明细 -->
		<view class="yt-list">
			<view class="yt-list-cell b-b">
				<text class="cell-tit clamp">商品金额</text>
				<text class="cell-tip">￥{{total | 1000}}</text>
			</view>
			<view class="yt-list-cell b-b">
				<text class="cell-tit clamp">运费</text>
				<text class="cell-tip">
					<block v-if="addressData">
						<block v-if="carriage > 0">
							{{carriage | 1000}}
						</block>
						<block v-else>
							免运费
						</block>
					</block>
					<block v-else>
						请选择收货地址
					</block>
				</text>
			</view>
			<view class="yt-list-cell desc-cell">
				<text class="cell-tit clamp">备注</text>
				<input class="desc" type="text" v-model="data.remark" placeholder="请填写备注信息" placeholder-class="placeholder" maxlength="200"/>
			</view>
		</view>
		
		<!-- 底部 -->
		<view class="footer">
			<view class="price-content">
				<text>实付款</text>
				<text class="price-tip">￥</text>
				<text class="price">{{outPocket | 1000}}</text>
			</view>
			<text class="submit" @click="submit">提交订单</text>
		</view>
	</view>
</template>

<script>
	import Shipping from '../../api/shipping'
	import GoodIndent from '../../api/goodIndent'
	import {mapMutations} from 'vuex'
	export default {
		data() {
			return {
				maskState: 0, //优惠券面板显示状态
				desc: '', //备注
				payType: 1, //1微信 2支付宝
				couponList: [
					{
						title: '新用户专享优惠券',
						price: 5,
					},
					{
						title: '庆五一发一波优惠券',
						price: 10,
					},
					{
						title: '优惠券优惠券优惠券优惠券',
						price: 15,
					}
				],
				goodList: [],
				addressData: {},
				carriage: 0,
				total: 0,
				outPocket: 0,
				data: {
					indentCommodity: [],
					address: {},
					remark: '',
					carriage: 0
				},
				order: []
			}
		},
		onLoad(option){
			this.loginCheck()
			this.loadData()
		},
		methods: {
			...mapMutations(['loginCheck']),
			//商品数据
			async loadData(){
				this.cartList = []
				this.invalidGood = []
				let cartList =  uni.getStorageSync('dsshopOrderList') || {}
				const that = this
				for(var k in cartList){
					this.order.push({
						id: cartList[k].good_id,
						number: cartList[k].number,
						freight_id: cartList[k].good.freight_id
					})
					cartList[k].checked = true
					cartList[k].loaded = 'loaded'
					if(cartList[k].good_sku){
						cartList[k].good_sku.skus.forEach(item=>{
							if(cartList[k].specification){
								cartList[k].specification+= item.v + ';'
							}else{
								cartList[k].specification = item.v + ';'
							}
							
						})
						cartList[k].specification = cartList[k].specification.substr(0,cartList[k].specification.length-1)
					}
					if(cartList[k].invalid === true){ //失效的商品
						cartList.splice(k,1)
					}
				}
				this.goodList = cartList
				that.data.indentCommodity = cartList
				that.calcTotal()  //计算总价
				that.getOne()
				
				
			},
			//获取默认收货地址
			getOne(){
				const that = this
				Shipping.defaultGet(this.order, function(res){
					that.addressData = res.shipping ? res.shipping : ''
					that.carriage = res.carriage ? res.carriage : 0
					that.outPocketTotal() //实付金额
				})
			},
			numberChange(data) {
				this.number = data.number;
			},
			changePayType(type){
				this.payType = type;
			},
			submit(){
				if(!this.addressData){
					this.$api.msg('请选择地址')
					return false
				}
				this.data.address = this.addressData
				this.data.carriage = this.carriage
				GoodIndent.create(this.data,function(res){
					uni.removeStorageSync('dsshopOrderList')
					uni.removeStorageSync('dsshopCartList')
					uni.redirectTo({
						url: '/pages/money/pay?id='+res
					})
				})
				
			},
			addAddress(){
				uni.navigateTo({
					url: `/pages/address/address?type=1`
				})
			},
			//地址选择回调
			refreshAddress(item){
				this.addressData = item
			},
			//计算总价
			calcTotal(){
				let list = this.goodList
				let total = 0
				for(var k in list){
					total += list[k].price * list[k].number
				}
				this.total = Number(total.toFixed(2))
			},
			//计算实付金额
			outPocketTotal(){
				let outPocket = 0
				outPocket = outPocket + this.total + this.carriage
				this.outPocket = Number(outPocket.toFixed(2))
			},
			stopPrevent(){}
		}
	}
</script>

